// scroll gallery init
function initCarousel() {
	jQuery('div.gallery').scrollGallery({
		mask: 'div.mask',
		slider: 'div.slideset',
		slides: 'div.slide',
		btnPrev: 'a.btn-prev, a.prev',
		btnNext: 'a.btn-next, a.next',
		pagerLinks: '.pagination li',
		onInit: function(that){
			if(that.gallery.hasClass('not-stretch')){
				var win = jQuery(window);
				win.load(function(){
					if(win.width() < 1023) {
						that.slides.css({width:''});
						that.gallery.css({width:that.slides.eq(0).width()})
						that.onWindowResize();
					}
					win.bind('resize orientationchange',function(){
						setTimeout(function(){
							if(win.width() < 1023) {
								that.slides.css({width:''});
								that.gallery.css({width:that.slides.eq(0).width()})
							}
							else {
								that.gallery.css({width:''});
							}
							that.onWindowResize();
						},200)
					})
				})
			}
			else{
				jQuery(window).load(function(){
					setTimeout(function(){
						that.onWindowResize();
					},200)
				})
			}
		},
		stretchSlideToMask: true,
		maskAutoSize: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500
	});
	jQuery('div.slideshow').scrollGallery({
		mask: '.tmpl-holder div.mask',
		slider: '>ul',
		slides: '>li',
		btnPrev: 'a.btn-prev, a.prev',
		btnNext: 'a.btn-next, a.next',
		pagerLinks: '.pagination li',
		paginationOn: true,
		stretchSlideToMask: false,
		maskAutoSize: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500,
		step:1
	});
	jQuery('div.bar-holder').scrollGallery({
		mask: 'div.bar-slide',
		slider: 'div.slideset',
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		stretchSlideToMask: true,
		maskAutoSize: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500
	});
	jQuery('div.slideshow-info').scrollGallery({
		mask: '.slide-nav div.pagination-holder',
		slider: '>ul',
		slides: '>li',
		pagerLinks: '.switch li',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		onInit: function(that){
			var win = jQuery(window);
			win.load(function(){
				if(win.width() > 1023) {
					that.slider.css({width:'',marginLeft:0});
					that.slides.css({width:''});
				}
				
				that.slides.each(function(ind){
					jQuery(this).bind('click',function(){
						that.currentStep = ind;
					})
				});
					
				win.bind('resize orientationchange',function(){
					setTimeout(function(){
						if(win.width() > 1023) {
							that.slider.css({width:'',marginLeft:0});
							that.slides.css({width:''});
						}
						else {
							that.gallery.css({width:''});
							that.slider.css({marginLeft: - that.mask.width() * that.currentStep});
							that.onWindowResize();
						}
					},200)
				})
			})
			that.gallery.bind('changeGallery',function(e,b){
				if(win.width() <= 1023) that.numSlide(b);
			})
		},
		stretchSlideToMask: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500,
		step:1
	});
}

